  (1,NULL,'01. Standard','<?php if (rex_request(\'type\',\'string\',\'\') == \'lightboxcontent\') { ?>\r\nREX_TEMPLATE[8]\r\n<?php die; } ?>\r\nREX_TEMPLATE[2 comment=\'PHP Header\']\r\nREX_TEMPLATE[3 comment=\'HTML Header]\r\n<?php\r\n\r\n$rootCategories = rex_category::getRootCategories(true);\r\n\r\nif (!empty($rootCategories)) {\r\n\r\n    $addon = rex_addon::get(\'demo_fullpage\');\r\n    \r\n    $_navtooltip = $addon->getConfig(\'shownavigationtooltip\');\r\n    $_use_subcats = $addon->getConfig(\'usesubcategories\');\r\n    $_autoscroll = $addon->getConfig(\'autoscrolling\');\r\n\r\n    $_mainnav = \'\';\r\n    $_mainnav .= \'	<nav class=\"main-navigation\">\' . PHP_EOL;\r\n    $_mainnav .= \'		<div class=\"pagelogo\">\' . PHP_EOL;\r\n    $_mainnav .= \'			<img src=\"\' . $fullpagedemo[\'logo\'] . \'\" alt=\"\' . $fullpagedemo[\'logotitle\'] . \'\" uk-tooltip=\"pos: bottom\" title=\"\' . $fullpagedemo[\'logotitle\'] . \'\">\' . PHP_EOL;\r\n    $_mainnav .= \'		</div>\' . PHP_EOL;\r\n    $_mainnav .= \'		<button class=\"hamburger\" type=\"button\"><span class=\"hamburger-box\"><span class=\"hamburger-inner\"></span></span></button>\' . PHP_EOL;\r\n    $_mainnav .= \'		<ul class=\"mainnav\">\' . PHP_EOL;\r\n    \r\n    $_content = \'\';\r\n    $even = true;\r\n\r\n    $sectionindex = 0 ;\r\n    foreach ($rootCategories as $cat) {\r\n        if ($cat->isOnline(true)) {\r\n            $sectionindex++;\r\n            \r\n            $href = rex_string::normalize(htmlspecialchars($cat->getName()));\r\n            $name = htmlspecialchars($cat->getName());\r\n\r\n            // Main navigation\r\n            $hideclass = \'\';\r\n            if ($cat->getValue(\'cat_fullpage_nomenu\') == \'|true|\') {\r\n                $hideclass = \' hide-mainnav\';\r\n            }\r\n            $_mainnav .= \'			<li data-menuanchor=\"\' . $href . \'\" class=\"mainnaventry-\' . $cat->getId() . $hideclass .\'\"><a href=\"#\' . $href . \'\" data-sectionid=\"\' . $cat->getId() . \'\">\' . $name . \'</a></li>\' . PHP_EOL;\r\n\r\n            // Section tooltip\r\n            if ($_navtooltip) {\r\n                $tooltip = \' data-tooltip=\"\' . $name . \'\"\';\r\n            } else {\r\n                $tooltip = \'\';\r\n            }\r\n\r\n            // Section background-color, background-image and background-video from category\r\n            $_style = array();\r\n            $video = \'\';\r\n            if ($cat->getValue(\'cat_fullpage_bgcolor\')) {\r\n                $_style[\'bgcolor\'] = \'background-color:\' . $cat->getValue(\'cat_fullpage_bgcolor\') . \';\';\r\n            }\r\n            if ($cat->getValue(\'cat_fullpage_bgimage\')) {\r\n                $_style[\'bgimage\'] = \'background-image:url(\' . rex_url::media($cat->getValue(\'cat_fullpage_bgimage\')) . \');\';\r\n            }\r\n            if ($cat->getValue(\'cat_fullpage_bgvideo\') <> \'\') {\r\n                $media = rex_media::get($cat->getValue(\'cat_fullpage_bgvideo\'));\r\n                if ($media) {\r\n                    $video = \' data-bgvideo=\"\' . rex_url::media($cat->getValue(\'cat_fullpage_bgvideo\')) . \'\"\';\r\n                    $video .= \' data-videotype=\"\' . $media->getType() . \'\"\';\r\n                }\r\n            }\r\n            \r\n            // Section background-color and background-image from article\r\n            $_artid = $cat->getId();\r\n            if ($_artid) {\r\n                $_art = new rex_article_content($_artid);\r\n                if ($_art->getValue(\'art_fullpage_bgcolor\')) {\r\n                    $_style[\'bgcolor\'] = \'background-color:\' . $_art->getValue(\'art_fullpage_bgcolor\') . \';\';\r\n                }\r\n                if ($_art->getValue(\'art_fullpage_bgimage\')) {\r\n                    $_style[\'bgimage\'] = \'background-image:url(\' . rex_url::media($_art->getValue(\'art_fullpage_bgimage\')) . \');\';\r\n                }\r\n                if ($_art->getValue(\'art_fullpage_bgvideo\') <> \'\') {\r\n                    $media = rex_media::get($_art->getValue(\'art_fullpage_bgvideo\'));\r\n                    if ($media) {\r\n                        $video = \' data-bgvideo=\"\' . rex_url::media($_art->getValue(\'art_fullpage_bgvideo\')) . \'\"\';\r\n                        $video .= \' data-videotype=\"\' . $media->getType() . \'\"\';\r\n                    }\r\n                }\r\n            }\r\n            \r\n            // Computed section style\r\n            $style = \' style=\"\' . implode(\' \', $_style) . \'\"\';\r\n            if ($style == \' style=\"\"\') $style = \'\';\r\n\r\n            $_slides = array();\r\n            $_pagecontent = \'\';\r\n\r\n            if (!empty($_articles = $cat->getArticles(true))) {\r\n                foreach ($_articles as $_art) {\r\n                    $_slides[] = $_art->getId();\r\n                }\r\n            }\r\n            \r\n            // Artikel der Unterkategorien als horizontale Slides\r\n            if ($_use_subcats) {\r\n                if (!empty($_categories = $cat->getChildren(true))) {\r\n                    foreach ($_categories as $_cat) {\r\n                        if (!empty($_articles = $_cat->getArticles(true))) {\r\n                            foreach ($_articles as $_art) {\r\n                                $_slides[] = $_art->getId();\r\n                            }\r\n                        }                        \r\n                    }\r\n                }\r\n            }\r\n\r\n            // Section class, autoheight ?\r\n            $_class = array();\r\n            $_class[] = \'section\';\r\n            $_class[] = \'section-idx-\' . $sectionindex;\r\n            $_class[] = \'section-\' . $cat->getId();\r\n            $_class[] = ($even == true) ? \'section-even\' : \'section-odd\';\r\n            if ($cat->getValue(\'cat_fullpage_autoheight\') == \'|true|\' && (count($_slides) == 1)) { // && !$_autoscroll\r\n                $_class[] = \'fp-auto-height\';\r\n            }\r\n            if (count($_slides) > 1) {\r\n                $_class[] = \'hasslides\';\r\n            }\r\n            $class = implode(\' \', $_class);\r\n\r\n            // Section\r\n            $_content .= \'	<!-- Fullpage-Slide \' . $cat->getId() . \' -->\' . PHP_EOL;\r\n            $_content .= \'	<section class=\"\' . $class . \'\" id=\"section\' . $cat->getId() . \'\" data-anchor=\"\' . $href . \'\" data-idx=\"\' . $sectionindex . \'\" data-cat=\"\' . $cat->getId() . \'\"\' . $tooltip . $style . $video . \'>\' . \"\\n\";\r\n            \r\n            // Nur ein Slide\r\n            if (count($_slides) == 1) {\r\n                foreach ($_slides as $_artid) {\r\n                    $_art = new rex_article_content($_artid);\r\n                    $_pagecontent .= \'		<div class=\"uk-container\">\' . PHP_EOL;\r\n                    $_pagecontent .= $_art->getArticle() . PHP_EOL;\r\n                    $_pagecontent .= \'		</div>\' . PHP_EOL;\r\n                }\r\n            }\r\n\r\n            // Horizontale Slides wenn mehrere Artikel in der Kategorie vorhanden sind, evtl. auch Unterkategorien laut Einstellungen\r\n            $slideindex = -1 ;\r\n            if (count($_slides) > 1) {\r\n                foreach ($_slides as $_artid) {\r\n                    $slideindex++;\r\n                    \r\n                    $_art = new rex_article_content($_artid);\r\n                    $href = rex_string::normalize(htmlspecialchars($_art->getValue(\'name\')));\r\n                    \r\n                    $_class = array();\r\n                    $_class[] = \'slide\';\r\n                    $_class[] = \'slide-\' . $cat->getId() . \'-\' . $_art->getValue(\'id\');\r\n                    $_class[] = \'slide-idx-\' . $slideindex;\r\n                    $class = implode(\' \', $_class);\r\n                    \r\n                    $_style = array();\r\n                    if ($_art->getValue(\'art_fullpage_bgcolor\')) {\r\n                        $_style[\'bgcolor\'] = \'background-color:\' . $_art->getValue(\'art_fullpage_bgcolor\') . \';\';\r\n                    }\r\n                    if ($_art->getValue(\'art_fullpage_bgimage\')) {\r\n                        $_style[\'bgimage\'] = \'background-image:url(\' . rex_url::media($_art->getValue(\'art_fullpage_bgimage\')) . \');\';\r\n                    }\r\n                    $video = \'\';\r\n                    if ($_art->getValue(\'art_fullpage_bgvideo\')) {\r\n                        $media = rex_media::get($_art->getValue(\'art_fullpage_bgvideo\'));\r\n                        if ($media) {\r\n                            $video = \' data-bgvideo=\"\' . rex_url::media($_art->getValue(\'art_fullpage_bgvideo\')) . \'\"\';\r\n                            $video .= \' data-videotype=\"\' . $media->getType() . \'\"\';\r\n                        }\r\n                    }\r\n                    \r\n                    $style = \' style=\"\' . implode(\' \', $_style) . \'\"\';\r\n                    $_pagecontent .= PHP_EOL . \'		<!-- Horizontal Slide \' . $_art->getValue(\'id\') . \' -->\' . PHP_EOL;\r\n                    $_pagecontent .= \'		<div class=\"\' . $class . \'\" id=\"slide\' . $_art->getValue(\'id\') . \'\" data-anchor=\"\' . $href . \'\"\' . $style . $video . \'>\' . PHP_EOL;\r\n                    $_pagecontent .= \'		<div class=\"uk-container\">\' . PHP_EOL;\r\n                    $_pagecontent .= $_art->getArticle() . PHP_EOL;\r\n                    $_pagecontent .= \'		</div>\' . PHP_EOL;\r\n                    $_pagecontent .= \'		</div>\' . PHP_EOL;\r\n                    $slide = \'slide\';\r\n                }\r\n            }\r\n\r\n            $_content .= $_pagecontent;\r\n\r\n            $_content .= \'	</section>\' . \"\\n\\n\";\r\n            $even = !$even;\r\n        }\r\n    }\r\n\r\n    $_mainnav .= \'		</ul>\' . PHP_EOL;\r\n    $_mainnav .= \'	</nav>\' . PHP_EOL;\r\n\r\n}\r\n?>\r\n\r\n<div class=\"pagewrapper\">\r\n\r\n<?php echo $_mainnav; ?>\r\n\r\n    <div class=\"fullpage<?php echo $_autoscroll === \'1\' ? \' autoscroll\' : \'\'; ?>\" data-themeid=\"<?php echo $addon->getConfig(\'theme\'); ?>\">\r\n\r\n<?php echo $_content; ?>\r\n    </div> <!-- /fullpage -->\r\n\r\n</div> <!-- /pagewrapper -->\r\n\r\nREX_TEMPLATE[4 comment=\'Footer\']',1,'2022-02-15 21:41:53','admin','2022-02-15 21:41:53','admin','{\"ctype\":[],\"modules\":{\"1\":{\"all\":\"1\"}},\"categories\":{\"all\":\"1\"}}',0),
  (2,NULL,'10. PHP Header','<?php\r\n// Error report should only be active during development. Deavtivate (0) on a live website\r\n// error_reporting(0);\r\nerror_reporting(E_ALL);\r\n\r\n// Is User not logged in?\r\nif (!rex_backend_login::hasSession()) {\r\n    // Is current article offline?\r\n    if ($this->getValue(\'status\') == 0) {\r\n        // redirect to 404 page\r\n        header(\'HTTP/1.1 301 Moved Permanently\');\r\n        header(\'Location: \' . rex_getUrl(rex_article::getNotFoundArticleId(), rex_clang::getCurrentId()));\r\n        die();\r\n    }\r\n}\r\n\r\n// set charset to utf8\r\nheader(\'Content-Type: text/html; charset=utf-8\');\r\n\r\n// setLocale is a language meta field, set your individual locale informations per language\r\nsetlocale(LC_ALL, rex_clang::getCurrent()->getValue(\'clang_setlocale\'));\r\n\r\n// Use article title as title-Tag, unless a custom title-tag is set\r\nif ($this->getValue(\'art_title\') != \'\') {\r\n    $title = htmlspecialchars($this->getValue(\'art_title\'));\r\n} else {\r\n    $title = htmlspecialchars($this->getValue(\'name\'));\r\n}\r\n\r\n// Keywords and description\r\n// If current article does not have keywords and description, take them from start article\r\nif ($this->getValue(\'art_keywords\') != \'\') {\r\n    $keywords = htmlspecialchars($this->getValue(\'art_keywords\'));\r\n} else {\r\n    $home = new rex_article_content(rex_article::getSiteStartArticleId());\r\n    $keywords = htmlspecialchars($home->getValue(\'art_keywords\'));\r\n}\r\n\r\nif ($this->getValue(\'art_description\') != \'\') {\r\n    $description = htmlspecialchars($this->getValue(\'art_description\'));\r\n} else {\r\n    $home = new rex_article_content(rex_article::getSiteStartArticleId());\r\n    $description = htmlspecialchars($home->getValue(\'art_description\'));\r\n}\r\n\r\n// Theme aus Addon-Einstellungen (addon demo_fullpage)\r\n$fullpagedemo[\'theme\'] = rex_addon::get(\'demo_fullpage\')->getConfig(\'theme\');\r\n$xtheme = rex_request(\'theme\', \'string\', \'\');\r\nif ($xtheme <> \'\' and file_exists(\'assets/addons/demo_fullpage/themes/\' . $xtheme)) {\r\n    $fullpagedemo[\'theme\'] = $xtheme;\r\n}\r\n\r\n// Logo aus Addon-Einstellungen (addon demo_fullpage)\r\n$fullpagedemo[\'logo\'] = rex_addon::get(\'demo_fullpage\')->getConfig(\'logo\');\r\nif ($fullpagedemo[\'logo\'] == \'\') {\r\n    if (file_exists(rex_url::base(\'assets/addons/demo_fullpage/themes/\' . $fullpagedemo[\'theme\'] . \'/img/\' . \'logo.svg\'))) {\r\n        $fullpagedemo[\'logo\'] = rex_url::base(\'assets/addons/demo_fullpage/themes/\' . $fullpagedemo[\'theme\'] . \'/img/\' . \'logo.svg\');\r\n    } else {\r\n        $fullpagedemo[\'logo\'] = rex_url::base(\'assets/addons/demo_fullpage/themes/\' . $fullpagedemo[\'theme\'] . \'/img/\' . \'logo.png\');\r\n    }\r\n} else {\r\n    $fullpagedemo[\'logo\'] = rex_url::base(\'media/\' . $fullpagedemo[\'logo\']);\r\n}\r\n$fullpagedemo[\'logotitle\'] = trim(rex::getServerName());\r\nif ($fullpagedemo[\'logotitle\'] == \'\') {\r\n    $fullpagedemo[\'logotitle\'] = \'Fullpage-Demo für das REDAXO CMS\';\r\n}\r\n?><!DOCTYPE html>',0,'2022-01-14 20:15:05','admin','2022-01-14 20:15:05','admin','{\"ctype\":[],\"modules\":{\"1\":{\"all\":\"1\"}},\"categories\":{\"all\":\"1\"}}',0),
  (3,NULL,'11. HTML-Header','<html lang=\"<?php echo rex_clang::getCurrent()->getCode(); ?>\">\r\n<head>\r\n\r\n<!--\r\n    Fullpage-Demo für REDAXO 5\r\n    https://redaxo.org/\r\n    https://friendsofredaxo.github.io/\r\n    https://github.com/FriendsOfREDAXO/demo_fullpage\r\n-->\r\n\r\n    <meta charset=\"utf-8\">\r\n    <meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\" />\r\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1\">\r\n\r\n    <title><?php echo $title; ?></title>\r\n    <meta name=\"keywords\" content=\"<?php echo $keywords; ?>\">\r\n    <meta name=\"description\" content=\"<?php echo $description; ?>\">\r\n\r\n    <link rel=\"shortcut icon\" href=\"<?php echo rex_url::base(\'assets/addons/demo_fullpage/themes/\' . $fullpagedemo[\'theme\'] . \'/img/favicon.ico\'); ?>\">\r\n    <link rel=\"icon\" type=\"image/png\" sizes=\"16x16\" href=\"<?php echo rex_url::base(\'assets/addons/demo_fullpage/themes/\' . $fullpagedemo[\'theme\'] . \'/img/favicon-16x16.png\'); ?>\">\r\n    <link rel=\"icon\" type=\"image/png\" sizes=\"16x16\" href=\"<?php echo rex_url::base(\'assets/addons/demo_fullpage/themes/\' . $fullpagedemo[\'theme\'] . \'/img/favicon-32x32.png\'); ?>\">\r\n\r\n    <link rel=\"stylesheet\" href=\"<?php echo rex_url::base(\'assets/addons/demo_fullpage/css/uikit.min.css\'); ?>\">\r\n    <link rel=\"stylesheet\" href=\"<?php echo rex_url::base(\'assets/addons/demo_fullpage/css/jquery.fullpage.min.css\'); ?>\">\r\n\r\n    <link rel=\"stylesheet\" href=\"<?php echo rex_url::base(\'assets/addons/demo_fullpage/themes/\' . $fullpagedemo[\'theme\'] . \'/css/theme.min.css\'); ?>\">\r\n\r\n</head>\r\n\r\n<body>\r\n\r\n<div class=\"loader\">Loading ...</div>',0,'2022-01-14 20:15:05','admin','2022-01-14 20:15:05','admin','{\"ctype\":[],\"modules\":{\"1\":{\"all\":\"1\"}},\"categories\":{\"all\":\"1\"}}',0),
  (4,NULL,'20. Footer','<!-- Fullpage-Options -->\r\n<script>\r\nvar fps_options = {\r\n<?php\r\n$addon = rex_addon::get(\'demo_fullpage\');\r\n$opts = array();\r\n    \r\n$opts[] = $addon->getConfig(\'showscrollbar\') ? \'  scrollBar: true\' : \'  scrollBar: false\';\r\n$opts[] = \'  responsiveWidth: \' . $addon->getConfig(\'responsivewidth\');\r\n$opts[] = \'  responsiveHeight: \' . $addon->getConfig(\'responsiveheight\');\r\n$opts[] = \'  scrollingSpeed: \' . $addon->getConfig(\'scrollingspeed\');\r\nif ($addon->getConfig(\'autoscrolling\')) {\r\n    $opts[] = \'  autoScrolling: true\';\r\n    $opts[] = \'  fitToSection: true\';\r\n} else {\r\n    $opts[] = \'  autoScrolling: false\';\r\n    $opts[] = \'  fitToSection: false\';\r\n}\r\nif ($addon->getConfig(\'shownavigation\')) {\r\n    $opts[] = \'  navigation: true\';\r\n    $opts[] = \'  navigationPosition: \\\'\' . $addon->getConfig(\'navigationposition\') . \'\\\'\';\r\n} else {\r\n    $opts[] = \'  navigation: false\';\r\n    $opts[] = \'  navigationPosition: \\\'\' . $addon->getConfig(\'navigationposition\') . \'\\\'\';\r\n}\r\n$opts[] = $addon->getConfig(\'showslidearrows\') ? \'  controlArrows: true\' : \'  controlArrows: false\';\r\nif ($addon->getConfig(\'showslidenavigation\')) {\r\n    $opts[] = \'  slidesNavigation: true\';\r\n    $opts[] = \'  slidesNavPosition: \\\'\' . $addon->getConfig(\'slidenavigationposition\') . \'\\\'\';\r\n} else {\r\n    $opts[] = \'  slidesNavigation: false\';\r\n    $opts[] = \'  slidesNavPosition: \\\'\' . $addon->getConfig(\'slidenavigationposition\') . \'\\\'\';\r\n}\r\n    \r\necho implode(\",\".PHP_EOL, $opts) . PHP_EOL;\r\n?>\r\n};\r\n</script>\r\n\r\n<!-- Javascript --> \r\n<script defer src=\"<?php echo rex_url::base(\'assets/addons/demo_fullpage/js/jquery.min.js\') ?>\"></script>\r\n<script defer src=\"<?php echo rex_url::base(\'assets/addons/demo_fullpage/js/jquery.fullpage.min.js\') ?>\"></script>\r\n<script defer src=\"<?php echo rex_url::base(\'assets/addon