  (6,NULL,'30. Download','<?php\r\n\r\nif (\'REX_VALUE[2]\' !== \'\') {\r\n    echo \'<REX_VALUE[3]>REX_VALUE[2]</REX_VALUE[3]>\' . PHP_EOL;\r\n}\r\n\r\nif (\'REX_MEDIALIST[1]\' !== \'\') {\r\n    echo \'<div class=\"uk-margin-bottom\">\' . PHP_EOL;\r\n    echo \'<div class=\"uk-grid-small uk-child-width-1-2@m uk-child-width-1-3@l uk-text-center\" uk-grid>\' . PHP_EOL;\r\n\r\n    // Find out file size\r\n    if (!function_exists(\'datei_groesse\')) {\r\n        /**\r\n         * @param string $URL\r\n         * @return string\r\n         */\r\n        function datei_groesse($URL)\r\n        {\r\n            $groesse = (float) filesize($URL);\r\n            if ($groesse < 1000) {\r\n                return number_format($groesse, 0, \',\', \'.\').\' Bytes\';\r\n            }\r\n            if ($groesse < 1000000) {\r\n                return number_format($groesse / 1024, 0, \',\', \'.\').\' kB\';\r\n            }\r\n            return number_format($groesse / 1048576, 0, \',\', \'.\').\' MB\';\r\n        }\r\n    }\r\n\r\n    // Find out icon\r\n    if (!function_exists(\'parse_icon\')) {\r\n        /**\r\n         * @param string $ext\r\n         * @return string\r\n         */\r\n        function parse_icon($ext)\r\n        {\r\n            switch (strtolower($ext)) {\r\n                case \'doc\': return \'<i class=\"fas fa-file-word\"></i>\';\r\n                case \'pdf\': return \'<i class=\"fas fa-file-pdf\"></i>\';\r\n                case \'zip\': return \'<i class=\"fas fa-file-archive\"></i>\';\r\n                case \'svg\': return \'<i class=\"fas fa-file-image\"></i>\';\r\n                case \'jpg\': return \'<i class=\"fas fa-file-image\"></i>\';\r\n                case \'png\': return \'<i class=\"fas fa-file-image\"></i>\';\r\n                case \'mp4\': return \'<i class=\"fas fa-file-video\"></i>\';\r\n                    // please add your own settings, e.g. with icons of Font-Awesome\r\n                default:\r\n                    return