\r\n                ];\r\n            ?>\r\n            <select name=\"REX_INPUT_VALUE[13]\" id=\"autoplay-video\" class=\"form-control\">\r\n            <?php foreach ($options as $k => $v) : ?>\r\n            <option value=\"<?= $k ?>\"<?php if (\'REX_VALUE[13]\' === (string) $k) {\r\n                echo \' selected=\"selected\"\';\r\n            } ?>><?= $v ?></option>\r\n            <?php endforeach ?>\r\n            </select>\r\n        </div>\r\n    </div>\r\n\r\n</fieldset>','2022-02-11 20:08:15','admin','2023-03-27 22:54:31','admin',NULL,0);
