  (1,NULL,'01. Standard','<?php if (\'lightboxcontent\' === rex_request(\'type\', \'string\', \'\')) { ?>\r\nREX_TEMPLATE[8 comment=\'lightbox-Template\']\r\n<?php exit;\r\n} ?>\r\nREX_TEMPLATE[2 comment=\'PHP Header\']\r\nREX_TEMPLATE[3 comment=\'HTML Header]\r\n<?php\r\n\r\n$rootCategories = rex_category::getRootCategories(true);\r\n\r\n$_mainnav = \'\';\r\n$_content = \'\';\r\n$_autoscroll = \'\';\r\n$horizontal_slide_titles = [];\r\n\r\n/** @var array<string> $fullpagedemo */\r\n\r\nif (null !== $rootCategories) {\r\n    $addon = rex_addon::get(\'demo_fullpage\');\r\n\r\n    $_navtooltip = $addon->getConfig(\'shownavigationtooltip\');\r\n    $_use_subcats = $addon->getConfig(\'usesubcategories\');\r\n    $_autoscroll = $addon->getConfig(\'autoscrolling\');\r\n\r\n    $_mainnav .= \'	<nav class=\"main-navigation\">\' . PHP_EOL;\r\n    $_mainnav .= \'		<div class=\"pagelogo\">\' . PHP_EOL;\r\n    $_mainnav .= \'			<img src=\"\' . $fullpagedemo[\'logo\'] . \'\" alt=\"\' . $fullpagedemo[\'logotitle\'] . \'\" uk-tooltip=\"pos: bottom\" title=\"\' . $fullpagedemo[\'logotitle\'] . \'\">\' . PHP_EOL;\r\n    $_mainnav .= \'		</div>\' . PHP_EOL;\r\n    $_mainnav .= \'		<button class=\"hamburger\" type=\"button\" name=\"menu\" aria-label=\"Hamburger menu\"><span class=\"hamburger-box\"><span class=\"hamburger-inner\"></span></span></button>\' . PHP_EOL;\r\n    $_mainnav .= \'		<ul class=\"mainnav\">\' . PHP_EOL;\r\n\r\n    $odd = true;\r\n    $horizontalodd = true;\r\n\r\n    $sectionindex = 0;\r\n    foreach ($rootCategories as $cat) {\r\n        if ($cat->isOnline()) {\r\n            ++$sectionindex;\r\n\r\n            $href = rex_string::normalize(htmlspecialchars($cat->getName()));\r\n            $name = htmlspecialchars($cat->getName());\r\n\r\n            // Main navigation\r\n            $hideclass = \'\';\r\n            if (\'|true|\' === $cat->getValue(\'cat_fullpage_nomenu\')) {\r\n                $hideclass = \' hide-mainnav\';\r\n            }\r\n            $_mainnav .= \'			<li data-menuanchor=\"\' . $href . \'\" class=\"mainnaventry-\' . $cat->getId() . $hideclass .\'\"><a href=\"#\' . $href . \'\" data-sectionid=\"\' . $cat->getId() . \'\">\' . $name . \'</a></li>\' . PHP_EOL;\r\n\r\n            // Section tooltip\r\n            if (\'\' !== $_navtooltip) {\r\n                $tooltip = \' data-tooltip=\"\' . $name . \'\"\';\r\n            } else {\r\n                $tooltip = \'\';\r\n            }\r\n\r\n            // Section background-color, background-image and background-video from category\r\n            $_style = [];\r\n            $video = \'\';\r\n            if (\'\' !== $cat->getValue(\'cat_fullpage_bgcolor\')) {\r\n                $_style[\'bgcolor\'] = \'background:\' . $cat->getValue(\'cat_fullpage_bgcolor\') . \';\';\r\n            }\r\n            if (\'\' !== $cat->getValue(\'cat_fullpage_bgimage\')) {\r\n                $_style[\'bgimage\'] = \'background-image:url(\' . rex_url::media((string) $cat->getValue(\'cat_fullpage_bgimage\')) . \');\';\r\n            }\r\n            if (\'\' !== $cat->getValue(\'cat_fullpage_bgvideo\')) {\r\n                $media = rex_media::get((string) $cat->getValue(\'cat_fullpage_bgvideo\'));\r\n                if (null !== $media) {\r\n                    $video = PHP_EOL . \' data-bgvideo=\"\' . rex_url::media((string) $cat->getValue(\'cat_fullpage_bgvideo\')) . \'\" data-videotype=\"\' . $media->getType() . \'\"\';\r\n                }\r\n            }\r\n\r\n            // Section background-color and background-image from article\r\n            $_artid = $cat->getId();\r\n            if (null !== $_artid) {\r\n                $_art = new rex_article_content($_artid);\r\n                if (\'\' !== $_art->getValue(\'art_fullpage_bgcolor\')) {\r\n                    $_style[\'bgcolor\'] = \'background:\' . $_art->getValue(\'art_fullpage_bgcolor\') . \';\';\r\n                }\r\n                if (\'\' !== $_art->getValue(\'art_fullpage_bgimage\')) {\r\n                    $_style[\'bgimage\'] = \'background-image:url(\' . rex_url::media((string) $_art->getValue(\'art_fullpage_bgimage\')) . \');\';\r\n                }\r\n                if (\'\' !== $_art->getValue(\'art_fullpage_bgvideo\')) {\r\n                    $media = rex_media::get((string) $_art->getValue(\'art_fullpage_bgvideo\'));\r\n                    if (null !== $media) {\r\n                        $video = PHP_EOL . \' data-bgvideo=\"\' . rex_url::media((string) $_art->getValue(\'art_fullpage_bgvideo\')) . \'\" data-videotype=\"\' . $media->getType() . \'\"\';\r\n                    }\r\n                }\r\n            }\r\n\r\n            // Computed section style\r\n            $style = \' data-bgstyle=\"\' . implode(\' \', $_style) . \'\"\';\r\n            if (\' style=\"\"\' === $style) {\r\n                $style = \'\';\r\n            }\r\n\r\n            $_slides = [];\r\n            $_pagecontent = \'\';\r\n\r\n            if (null !== ($_articles = $cat->getArticles(true))) {\r\n                foreach ($_articles as $_artx) {\r\n                    $_slides[] = $_artx->getId();\r\n                }\r\n            }\r\n\r\n            // Artikel der Unterkategorien als horizontale Slides\r\n            if (\'\' !== $_use_subcats) {\r\n                if (null !== ($_categories = $cat->getChildren(true))) {\r\n                    foreach ($_categories as $_cat) {\r\n                        if (null !== ($_articles = $_cat->getArticles(true))) {\r\n                            foreach ($_articles as $_artx) {\r\n                                $_slides[] = $_artx->getId();\r\n                            }\r\n                        }\r\n                    }\r\n                }\r\n            }\r\n\r\n            // Section class, autoheight ?\r\n            $_class = [];\r\n            $_class[] = \'section\';\r\n            $_class[] = \'section-\' . $cat->getId();\r\n            $_class[] = \'section-idx-\' . $sectionindex;\r\n            $_class[] = (true === $odd) ? \'section-odd\' : \'section-even\';\r\n            if (\'|true|\' === $cat->getValue(\'cat_fullpage_autoheight\') && (1 === count($_slides))) { // && !$_autoscroll\r\n                $_class[] = \'fp-auto-height\';\r\n            }\r\n            $_class[] = \'fp-auto-height-responsive\';\r\n\r\n            if (count($_slides) > 1) {\r\n                $_class[] = \'hasslides\';\r\n            }\r\n            $class = implode(\' \', $_class);\r\n\r\n            // Section\r\n            $_content .= \'	<!-- Fullpage-Slide \' . $cat->getId() . \' -->\' . PHP_EOL;\r\n            $_content .= \'	<section class=\"\' . $class . \'\" id=\"section\' . $cat->getId() . \'\" data-anchor=\"\' . $href . \'\" data-idx=\"\' . $sectionindex . \'\" data-cat=\"\' . $cat->getId() . \'\"\' . $tooltip . $style . $video . \'>\' . PHP_EOL;\r\n\r\n            // Nur ein Slide\r\n            if (1 === count($_slides)) {\r\n                foreach ($_slides as $_artidx) {\r\n                    $_art = new rex_article_content($_artidx);\r\n                    $_pagecontent .= \'		<div class=\"uk-container\">\' . PHP_EOL;\r\n                    $_pagecontent .= $_art->getArticle() . PHP_EOL;\r\n                    $_pagecontent .= \'		</div>\' . PHP_EOL;\r\n                }\r\n            }\r\n\r\n            // Horizontale Slides wenn mehrere Artikel in der Kategorie vorhanden sind, evtl. auch Unterkategorien laut Einstellungen\r\n            $slideindex = -1;\r\n            if (count($_slides) > 1) {\r\n                foreach ($_slides as $_artidx) {\r\n                    ++$slideindex;\r\n\r\n                    $_art = new rex_article_content($_artidx);\r\n                    $href = rex_string::normalize(htmlspecialchars((string) $_art->getValue(\'name\')));\r\n\r\n                    // Slide tooltip\r\n                    if (\'\' !== $_navtooltip) {\r\n                        $tooltip = \' data-tooltip=\"\' . $_art->getValue(\'name\') . \'\"\';\r\n                    } else {\r\n                        $tooltip = \'\';\r\n                    }\r\n\r\n                    $_class = [];\r\n                    $_class[] = \'slide\';\r\n                    $_class[] = \'slide-\' . $_art->getValue(\'id\');\r\n                    $_class[] = \'slide-\' . $cat->getId() . \'-\' . $_art->getValue(\'id\');\r\n                    $_class[] = \'slide-idx-\' . $slideindex;\r\n                    $_class[] = (true === $horizontalodd) ? \'slide-odd\' : \'slide-even\';\r\n                    $class = implode(\' \', $_class);\r\n\r\n                    $_style = [];\r\n                    if (\'\' !== $_art->getValue(\'art_fullpage_bgcolor\')) {\r\n                        $_style[\'bgcolor\'] = \'background:\' . $_art->getValue(\'art_fullpage_bgcolor\') . \';\';\r\n                    }\r\n                    if (\'\' !== $_art->getValue(\'art_fullpage_bgimage\')) {\r\n                        $_style[\'bgimage\'] = \'background-image:url(\' . rex_url::media((string) $_art->getValue(\'art_fullpage_bgimage\')) . \');\';\r\n                    }\r\n                    $video = \'\';\r\n                    if (\'\' !== $_art->getValue(\'art_fullpage_bgvideo\')) {\r\n                        $media = rex_media::get((string) $_art->getValue(\'art_fullpage_bgvideo\'));\r\n                        if (null !== $media) {\r\n                            $video = \' data-bgvideo=\"\' . rex_url::media((string) $_art->getValue(\'art_fullpage_bgvideo\')) . \'\" data-videotype=\"\' . $media->getType() . \'\"\';\r\n                        }\r\n                    }\r\n\r\n                    $bgstyle = \'\';\r\n                    if (\'\' !== implode(\' \', $_style)) {\r\n                        $bgstyle = \' data-bgstyle=\"\' . implode(\' \', $_style) . \'\"\';\r\n                    }\r\n                    $_pagecontent .= PHP_EOL . \'		<!-- Horizontal Slide \' . $_art->getValue(\'id\') . \' -->\' . PHP_EOL;\r\n                    $_pagecontent .= \'		<div class=\"\' . $class . \'\" id=\"slide\' . $_art->getValue(\'id\') . \'\" data-anchor=\"\' . $href . \'\"\' . $tooltip . $bgstyle . $video . \'>\' . PHP_EOL;\r\n                    $_pagecontent .= \'		<div class=\"uk-container\">\' . PHP_EOL;\r\n                    $_pagecontent .= $_art->getArticle() . PHP_EOL;\r\n                    $_pagecontent .= \'		</div>\' . PHP_EOL;\r\n                    $_pagecontent .= \'		</div>\' . PHP_EOL;\r\n                    $slide = \'slide\';\r\n\r\n                    // $data = [ \"item\" =>  [\"id\", \"123456\", \"name\", \"adam\"]  ];\r\n                    $horizontal_slide_titles[$cat->getId()][] = [$cat->getId(), $_art->getValue(\'name\')];\r\n\r\n                    $horizontalodd = !$horizontalodd;\r\n                }\r\n            }\r\n\r\n            $_content .= $_pagecontent;\r\n\r\n            $_content .= \'	</section>\' . PHP_EOL . PHP_EOL;\r\n            $odd = !$odd;\r\n        }\r\n    }\r\n\r\n    $_mainnav .= \'		</ul>\' . PHP_EOL;\r\n    $_mainnav .= \'	</nav>\' . PHP_EOL;\r\n}\r\n?>\r\n\r\n<div class=\"pagewrapper\">\r\n\r\n<?= $_mainnav ?>\r\n\r\n    <div class=\"fullpage<?= \'1\' === $_autoscroll ? \' autoscroll\' : \'\' ?>\" data-themeid=\"<?= $fullpagedemo[\'theme\'] ?>\">\r\n\r\n<?= $_content ?>\r\n    </div> <!-- /fullpage -->\r\n\r\n</div> <!-- /pagewrapper -->\r\n\r\nREX_TEMPLATE[4 comment=\'Footer\']',1,'2023-03-15 21:23:52','admin','2023-03-27 22:32:46','admin','{\"ctype\":[],\"modules\":{\"1\":{\"all\":\"1\"}},\"categories\":{\"all\":\"1\"}}',0),
  (2,NULL,'10. PHP Header','<?php\r\n// Error report should only be active during development. Deavtivate (0) on a live website\r\n// error_reporting(0);\r\nerror_reporting(E_ALL);\r\n\r\n// Is User not logged in?\r\nif (!rex_backend_login::hasSession()) {\r\n    // Is current article offline?\r\n    if (0 === $this->getValue(\'status\')) {\r\n        // redirect to 404 page\r\n        header(\'HTTP/1.1 301 Moved Permanently\');\r\n        header(\'Location: \' . rex_getUrl(rex_article::getNotfoundArticleId(), rex_clang::getCurrentId()));\r\n        exit;\r\n    }\r\n}\r\n\r\n// set charset to utf8\r\nheader(\'Content-Type: text/html; charset=utf-8\');\r\n\r\n// setLocale is a language meta field, set your individual locale informations per language\r\nsetlocale(LC_ALL, \'\'.rex_clang::getCurrent()->getValue(\'clang_setlocale\'));\r\n\r\n// Use article title as title-Tag, unless a custom title-tag is set\r\nif (\'\' !== $this->getValue(\'art_title\') && null !== $this->getValue(\'art_title\')) {\r\n    $title = htmlspecialchars($this->getValue(\'art_title\'));\r\n} else {\r\n    $title = htmlspecialchars($this->getValue(\'name\'));\r\n}\r\n\r\n// Keywords and description\r\n// If current article does not have keywords and description, take them from start article\r\nif (\'\' !== $this->getValue(\'art_keywords\') && null !== $this->getValue(\'art_keywords\')) {\r\n    $keywords = htmlspecialchars($this->getValue(\'art_keywords\'));\r\n} else {\r\n    $home = new rex_article_content(rex_article::getSiteStartArticleId());\r\n    $keywords = htmlspecialchars((string) $home->getValue(\'art_keywords\'));\r\n}\r\n\r\nif (\'\' !== $this->getValue(\'art_description\')) {\r\n    $description = htmlspecialchars((string) $this->getValue(\'art_description\'));\r\n} else {\r\n    $home = new rex_article_content(rex_article::getSiteStartArticleId());\r\n    $description = htmlspecialchars((string) $home->getValue(\'art_description\'));\r\n}\r\n\r\n/** @var array<string> $fullpagedemo */\r\n\r\n// Theme aus Addon-Einstellungen (addon demo_fullpage)\r\n$fullpagedemo[\'theme\'] = rex_addon::get(\'demo_fullpage\')->getConfig(\'theme\');\r\nif (\'project:\' === substr($fullpagedemo[\'theme\'], 0, 8)) {\r\n    $fullpagedemo[\'theme\'] = str_replace(\'project:\', \'\', $fullpagedemo[\'theme\']);\r\n}\r\n$xtheme = rex_request(\'theme\', \'string\', \'\');\r\nif (\'\' !== $xtheme && file_exists(rex_path::addonAssets(\'demo_fullpage\', \'themes/\' . $xtheme))) {\r\n    $fullpagedemo[\'theme\'] = $xtheme;\r\n}\r\n\r\n// Logo aus Addon-Einstellungen (addon demo_fullpage)\r\n$fullpagedemo[\'logo\'] = rex_addon::get(\'demo_fullpage\')->getConfig(\'logo\');\r\nif (\'\' === $fullpagedemo[\'logo\']) {\r\n    if (file_exists(rex_path::addonAssets(\'demo_fullpage\', \'themes/\' . $fullpagedemo[\'theme\'] . \'/img/logo.svg\'))) {\r\n        $fullpagedemo[\'logo\'] = rex_url::base(\'assets/addons/demo_fullpage/themes/\' . $fullpagedemo[\'theme\'] . \'/img/logo.svg\');\r\n    } else {\r\n        $fullpagedemo[\'logo\'] = rex_url::base(\'assets/addons/demo_fullpage/themes/\' . $fullpagedemo[\'theme\'] . \'/img/logo.png\');\r\n    }\r\n} else {\r\n    $fullpagedemo[\'logo\'] = rex_url::base(\'media/\' . $fullpagedemo[\'logo\']);\r\n}\r\n$fullpagedemo[\'logotitle\'] = trim(rex::getServerName());\r\nif (\'\' === $fullpagedemo[\'logotitle\']) {\r\n    $fullpagedemo[\'logotitle\'] = \'Fullpage-Demo für das REDAXO CMS\';\r\n}\r\n?><!DOCTYPE html>',0,'2022-07-12 23:44:52','admin','2023-03-27 22:32:01','admin','{\"ctype\":[],\"modules\":{\"1\":{\"all\":\"1\"}},\"categories\":{\"all\":\"1\"}}',0),
