  (5,NULL,'99. Download','<?php\r\n\r\n$file_name = strtolower((string) preg_replace(\'/[^a-zA-Z0-9.\\-\\$\\+]/\', \'_\', rex_request(\'file\', \'string\')));\r\n$file_type = \'application/octetstream\';\r\n$file_path = rex_path::media($file_name);\r\n\r\nif (\'\' !== $file_name) {\r\n    $fp = @fopen($file_path, \'r\');\r\n    if (false !== $fp) {\r\n        header(\'Expires: Mon, 01 Jan 2000 01:01:01 GMT\'); // Date in the past\r\n        header(\'Last-Modified: \' . gmdate(\'D, d M Y H:i:s\') . \' GMT\'); // always modified\r\n        header(\'Cache-Control: no-store, no-cache, must-revalidate\'); // HTTP/1.1\r\n        header(\'Cache-Control: post-check=0, pre-check=0\', false);\r\n        header(\'Cache-Control: private\');\r\n        header(\'Pragma: no-cache\');\r\n        header(\'Content-Type: $file_type; name=\"\' . $file_name . \'\"\');\r\n        header(\'Content-Disposition: attachment; filename=\"\' . $file_name . \'\"\');\r\n        rex_response::cleanOutputBuffers();\r\n        fpassthru($fp);\r\n        fclose($fp);\r\n    }\r\n    exit;\r\n}\r\n\r\necho \'<div class=\"warning\"\">Download failed</div>\';\r\n',1,'2022-02-24 21:14:06','admin','2023-04-01 22:52:14','admin','{\"ctype\":[],\"modules\":{\"1\":{\"all\":\"1\"}},\"categories\":{\"all\":\"1\"}}',0),
